upDown c = case c of '(' -> (1+)
                     ')' -> subtract 1
                     otherwise -> error "Invalid Character"

findFirstDescent :: (Num a, Eq a, Num b) => (a, b, Bool) -> Char -> (a, b, Bool)
findFirstDescent (level, position, False) char = let newLev = (upDown char) level
                                                 in case newLev of (-1) -> (0, position, True)
                                                                   otherwise -> (newLev, position+1, False)
findFirstDescent (_, pos, True) _ = (0, pos, True)

main :: IO ()
main = do
  print $ foldl (\acc x -> (upDown x) acc) 0 input
  print $ foldl findFirstDescent (0, 1, False) input
